mapper.select:
SELECT 

FROM sys_order a

WHERE a.id = #{id}

mapper.sql:
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.cid = #{cid}
AND a.producttype = #{producttype}
AND a.product LIKE #{product}||'%' #{product}+'%' concat(#{product},'%')
AND a.customerPhone LIKE #{product}||'%' #{product}+'%' concat(#{product},'%')
AND a.order_date BETWEEN #{beginOrderDate} AND #{endOrderDate}
AND a.customer_name LIKE #{customerName}||'%' #{customerName}+'%' concat(#{customerName},'%')
AND a.status = #{status}

mapper.select:
SELECT 

FROM sys_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
